import React from 'react';

import MoviesList from './components/MoviesList';
import './App.css';
import { useState } from 'react';

function App() {


  const [movies, setmovies]  = useState([])
  const[isloading , setloading]= useState(false)

 async function fetchmoviehandler ()  {
  setloading(true)
 const response = fetch('https://swapi.dev/api/films')
   const data =  response.json();



   const transformedmoviesdata = data.results.map(movieData =>{
    return {
      id: movieData.episode_id,
      title: movieData.title,
      openingText: movieData.opening_crawl,
      releaseDate: movieData.elease_date

    };
   })
   setmovies(transformedmoviesdata)
   setloading(false)





 };



  return (
    <React.Fragment>
      <section>
        <button onClick={fetchmoviehandler}  >Fetch Movies</button>
      </section>
      <section>
       {!isloading &&<MoviesList movies={movies} />} 
       {!isloading &&  movies.length===0 && <p> Found no movie</p> }
       {isloading && <p>loading....wait for a sec</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
